## 1 kb
std_r2_1kb <- as.data.frame(r2_exp_models %>% 
                              filter(., scale==1e+3) %>% 
                              dplyr::select(c(u_avg, B_avg,
                                              mult_r_u,
                                              shapes_rate_u,
                                              shapes_rate_r,
                                              avg_rec_spans,
                                              avg_mut_spans,
                                              num_exons)) %>%
                              apply(., 2, function(x) (x-mean(x)) / sd(x)))

m_u_1kb <- lm(u_avg ~ (mult_r_u +
                       shapes_rate_u +
                       shapes_rate_r +
                       avg_rec_spans + 
                       avg_mut_spans +
