m_B_1kb <- lm(B_avg ~ (mult_r_u +
                       shapes_rate_u + 
                       shapes_rate_r + 
                       avg_rec_spans +
                       avg_mut_spans +
                       num_exons)^2, data=std_r2_1kb)

summary(m_u_1kb)
summary(m_B_1kb)

